import React, { useEffect, useState } from 'react';
import './Dashboard.css';
import { database } from '../../firebasecon';
import { addDoc, collection, deleteDoc, doc, getDocs, updateDoc } from 'firebase/firestore';
import { Navigation } from '../navigation';

function Dashboard() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [id, setId] = useState('');
  const [show, setShow] = useState(false);
  const [val, setVal] = useState([]);
  const value = collection(database, "policeOfficers");

  useEffect(() => {
    getData();
  }, []); // Only run once on component mount

  const getData = async () => {
    const dbVal = await getDocs(value);
    setVal(dbVal.docs.map(doc => ({ ...doc.data(), id: doc.id })));
  }

  const handleCreate = async () => {
    await addDoc(value, { Email: email, Password: password });
    setEmail("");
    setPassword("");
    getData(); // Update local state after creating
  }

  const handleDelete = async (id) => {
    const deleteVal = doc(database, "policeOfficers", id);
    await deleteDoc(deleteVal);
    getData(); // Update local state after deleting
  }

  const handleEdit = async (id, Email, Password) => {
    setEmail(Email);
    setPassword(Password);
    setId(id);
    setShow(true);
  }

  const handleUpdate = async () => {
    const updateData = doc(database, "demo", id);
    await updateDoc(updateData, { Email: email, Password: password });
    setShow(false);
    setEmail("");
    setPassword("");
    getData(); // Update local state after updating
  }

  return (
    <div className='container'>
      <Navigation />
      <div className="content">
        <div className="container-box">
          <input value={email} onChange={(e) => setEmail(e.target.value)} className='input-box' placeholder='Email' /><br />
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className='input-box' placeholder='Password' /> <br />
          {!show ? <button onClick={handleCreate} className='create-box'>Create</button> :
            <button onClick={handleUpdate} className='update-box'>Update</button>}
        </div>

        <table>
          <thead>
            <tr>
              <th>Email</th>
              <th>Password</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {val.map(values =>
              <tr key={values.id}>
                <td>{values.Email}</td>
                <td>{values.Password}</td>
                <td>
                  <button onClick={() => handleDelete(values.id)} className='delete-box'>Delete</button>
                  <button onClick={() => handleEdit(values.id, values.Email, values.Password)} className='edit-box'>Edit</button>
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Dashboard;
