import {Navigation} from '../navigation'
import React, { useState, useEffect } from 'react';
import { signUpPoliceOfficer } from '../../firebasecon';
import firebase from 'firebase/app'

const Dashboard = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [policeOfficers, setPoliceOfficers] = useState([]);

  useEffect(() => {
    fetchPoliceOfficers();
  }, []);

  const fetchPoliceOfficers = async () => {
    try {
      const db = firebase.firestore();
      const snapshot = await db.collection('policeOfficers').get();
      const officers = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setPoliceOfficers(officers);
    } catch (error) {
      console.error('Error fetching police officers:', error);
    }
  };

  const handleAddPoliceOfficer = async () => {
    try {
      await signUpPoliceOfficer(email, password);
      const newOfficer = { email };
      setPoliceOfficers([...policeOfficers, newOfficer]);
      setEmail('');
      setPassword('');
    } catch (error) {
      console.error('Error adding police officer:', error);
    }
  };

  return (
    <div>
      <Navigation />
      <h2>Police Commissioner Dashboard</h2>
      <div>
        <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
        <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
        <button onClick={handleAddPoliceOfficer}>Add Police Officer</button>
      </div>
      <div>
        <h3>Police Officers</h3>
        <ul>
          {policeOfficers.map((officer, index) => (
            <li key={index}>{officer.email}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
