import React, { useEffect, useState } from 'react'
import './Dashboard.css'
import { database } from '../../firebasecon'
import { addDoc, collection, deleteDoc, doc, getDocs, updateDoc } from 'firebase/firestore'
import {Navigation} from '../navigation'

function Dashboard(){
    const [email,setEmail] =useState('')
    const [password,setPassword] =useState('')
    const [id,setId] =useState('')

    const [show,setShow] =useState(false)
    const [val,setVal] =useState([])
    const value = collection(database,"policeOfficers")


    useEffect(()=>{
        const getData= async()=>{
          const dbVal = await getDocs(value)
          setVal(dbVal.docs.map(doc=>({...doc.data(),id:doc.id})))
        }
        getData()
    })

    const handleCreate =async()=>{
        await addDoc(value,{Email:email,Password:password})
        setEmail("")
        setPassword("")
    }

    const handleDelete =async(id)=>{
       const deleteVal =  doc(database,"policeOfficers",id)
       await deleteDoc(deleteVal)
    }

    const handleEdit =async(id,name1,name2)=>{
        setEmail(name1)
        setPassword(name2)
        setId(id)
        setShow(true)
    }

    const handleUpdate =async()=>{
        const updateData = doc(database,"demo",id)
        await updateDoc(updateData,{name1:email,name2:password})
        setShow(false)
        setEmail("")
        setPassword("")
    }

    return(
        <div className='container'>
          <Navigation />
          <div className="container-box">
           <input value={email} onChange={(e) => setEmail(e.target.value)} className='input-box' placeholder='Email'/><br/>
           <input value={password} onChange={(e) => setPassword(e.target.value)} className='input-box' placeholder='Password'/> <br/>
           {!show?<button onClick={handleCreate} className='create-box'>Create</button>:
           <button onClick={handleUpdate}>Update</button>}
           </div>
           {
            val.map(values=>
            <div>
                <h1>{values.name1}</h1>
                <h1>{values.name2}</h1>
                <button onClick={()=>handleDelete(values.id)} className='delete-box'>Delete</button>
                <button onClick={()=>handleEdit(values.id,values.name1,values.name2)}className='edit-box'>Edit</button>
            </div>)
           }
        </div>
    )
}
export default Dashboard;
