import { FaUser } from 'react-icons/fa';
import './Login.css';
import { useState } from 'react';
import { createUserWithEmailAndPassword, onAuthStateChanged, signInWithEmailAndPassword, signOut } from 'firebase/auth';
import { auth } from '../../firebase-config';
import { useNavigate } from 'react-router-dom'; 


function Login() {

  const [loginEmail, setLoginEmail] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const [user, setUser] = useState({}); 
  const navigate = useNavigate(); 

  onAuthStateChanged(auth, (currentUser) => {
    setUser(currentUser);
  });

  const login = async () => {
    try {
      const user = await signInWithEmailAndPassword(
        auth,
        loginEmail,
        loginPassword
      );
      console.log(user);
      navigate('/home'); 
    } catch (error) {
      console.log(error.message);
    }
  };

  const logout = async () => {
    await signOut(auth);
  };


  return (
    <>

      <div className="box">
        <form autoComplete="off">
          <h2>Sign in</h2>
          <div className="inputBox">
            <input type="text" required="required" onChange={(event) => { setLoginEmail(event.target.value) }} />
            <span>Email</span>
            <i></i>
          </div>
          <div className="inputBox">
            <input type="password" required="required" onChange={(event) => { setLoginPassword(event.target.value) }} />
            <span>Password</span>
            <i></i>
          </div>
          {/* <div class="links">
          <a href="#">Forgot Password ?</a>
          <a href="#">Signup</a>
        </div> */}
          <input type="submit" onClick={login} value="Login" className='submitForm' />
        </form>
      </div>


    </>
  )
}

export default Login;
