import React, { useEffect, useState } from 'react';
import './Dashboard.css';
import { database } from '../../firebasecon';
import { addDoc, collection, deleteDoc, doc, getDocs, updateDoc } from 'firebase/firestore';
import { Navigation } from '../navigation';

function Dashboard() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [id, setId] = useState('');
  const [show, setShow] = useState(false);
  const [val, setVal] = useState([]);
  const value = collection(database, "policeOfficers");

  useEffect(() => {
    getData();
  }, []); // Only run once on component mount

  const getData = async () => {
    const dbVal = await getDocs(value);
    setVal(dbVal.docs.map(doc => ({ ...doc.data(), id: doc.id })));
  }

  const handleCreate = async () => {
    await addDoc(value, { Email: email, Password: password });
    setEmail("");
    setPassword("");
    getData(); // Update local state after creating
  }

  const handleDelete = async (id) => {
    await deleteDoc(doc(database, "policeOfficers", id));
    getData(); // Update local state after deleting
  }

  const handleEdit = (id, Email, Password) => {
    setEmail(Email);
    setPassword(Password);
    setId(id);
    setShow(true);
  }

  const handleUpdate = async () => {
    await updateDoc(doc(database, "policeOfficers", id), { Email: email, Password: password });
    setShow(false);
    setEmail("");
    setPassword("");
    getData(); // Update local state after updating
  }

  return (
    <div style={{ backgroundColor: '#333', color: '#ffffff',height: '100%' }}>
    <div className='container'style={{ backgroundColor: '#333', color: '#ffffff' }}>
      <Navigation />
      <div className="content"style={{ backgroundColor: '#333' }}>
        <div className="container-box">
          <input value={email} onChange={(e) => setEmail(e.target.value)} className='input-box' placeholder='Email' style={{color:'#000000'}}/><br />
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className='input-box' placeholder='Password'  style={{color:'#000000'}}/> <br />
          {!show ? <button onClick={handleCreate} className='create-box'>Create</button> :
            <button onClick={handleUpdate} className='update-box'>Update</button>}
        </div><br/><br/>
        <h2>Police Officers List</h2>
        <table style={{ backgroundColor: '#333', color: '#ffffff' }}>
          <thead>
            <tr>
              <th>Email</th>
              <th>Password</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {val.map(values =>
              <tr key={values.id}>
                <td>{values.Email}</td>
                <td>{values.Password}</td>
                <td>
                  <button onClick={() => handleDelete(values.id)} className='delete-box'>Delete</button>
                  <button onClick={() => handleEdit(values.id, values.Email, values.Password)} className='edit-box'>Edit</button>
                </td>
              </tr>
            )}
          </tbody>
        </table><br/><br/><br/>

        <h2>Police Officers Details</h2>
        <table style={{ backgroundColor: '#333', color: '#ffffff' }}>
          <thead>
            <tr>
              <th>Email</th>
              <th>Password</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {val.map(values =>
              <tr key={values.id}>
                <td>{values.Email}</td>
                <td>{values.Password}</td>
                <td>
                  <button onClick={() => handleDelete(values.id)} className='delete-box'>Delete</button>
                  <button onClick={() => handleEdit(values.id, values.Email, values.Password)} className='edit-box'>Edit</button>
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
    </div>
  );
}

export default Dashboard;
