import firebase from 'firebase/app';
import 'firebase/firestore';
import 'firebase/auth';

const firebaseConfig = {
    apiKey: "AIzaSyB_XYHVjAEGA73bpwGfUxKmr9p07Z4E66w",
    authDomain: "resq-bf5ee.firebaseapp.com",
    projectId: "resq-bf5ee",
    storageBucket: "resq-bf5ee.appspot.com",
    messagingSenderId: "562984630117",
    appId: "1:562984630117:web:b8e2aebbf8a0fd1f0a938c",
    measurementId: "G-EGKBWF0QJ2"
};

firebase.initializeApp(firebaseConfig);

// Function to sign up a police officer
const signUpPoliceOfficer = (email, password) => {
  return firebase.auth().createUserWithEmailAndPassword(email, password);
};

export default { signUpPoliceOfficer };
